   471→// loggingMiddleware wraps an HTTP handler with request logging
   472→func loggingMiddleware(next http.Handler) http.Handler {
   473→	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
   474→		start := time.Now()
   475→
   476→		log.Printf("[MCP] %s %s from %s", r.Method, r.URL.Path, r.RemoteAddr)
