   490→// Start starts the MCP server
   491→func (s *Server) Start(ctx context.Context) error {
   492→	// Create HTTP/SSE handler for MCP protocol with JSON response option
   493→	opts := &mcp.StreamableHTTPOptions{
   494→		JSONResponse: true, // Return JSON instead of SSE
   495→	}
   496→	handler := mcp.NewStreamableHTTPHandler(
   497→		func(*http.Request) *mcp.Server { return s.mcpServer },
   498→		opts,
   499→	)
   500→
   501→	// Wrap handler with logging middleware
   502→	loggedHandler := loggingMiddleware(handler)
   503→
   504→	// Create HTTP server
   505→	mux := http.NewServeMux()
   506→	mux.Handle(
   507→		"/mcp",
   508→		loggedHandler,
   509→	)
   510→
   511→	// Add health check endpoint
   512→	mux.HandleFunc(
   513→		"/health",
   514→		func(
   515→			w http.ResponseWriter,
   516→			r *http.Request,
   517→		) {
   518→			w.WriteHeader(http.StatusOK)
   519→			fmt.Fprintf(
   520→				w,
   521→				"OK\n",
   522→			)
   523→		},
   524→	)
   525→
   526→	s.httpServer = &http.Server{
   527→		Addr: fmt.Sprintf(
   528→			"127.0.0.1:%d",
   529→			s.port,
   530→		),
   531→		Handler: mux,
   532→	}
   533→
   534→	log.Printf(
   535→		"Starting MCP evaluation server on http://127.0.0.1:%d/mcp\n",
   536→		s.port,
   537→	)
   538→	log.Printf(
