   540→		s.port,
   541→	)
   542→
   543→	// Start server in a goroutine
   544→	errChan := make(
   545→		chan error,
   546→		1,
   547→	)
   548→	go func() {
   549→		if err := s.httpServer.ListenAndServe(); err != nil && err != http.ErrServerClosed {
   550→			errChan <- err
   551→		}
   552→	}()
   553→
   554→	// Wait for context cancellation or server error
   555→	select {
   556→	case err := <-errChan:
   557→		return fmt.Errorf(
   558→			"server error: %w",
   559→			err,
   560→		)
   561→	case <-ctx.Done():
   562→		return s.Shutdown(context.Background())
   563→	}
   564→}
