   458→	schema interface{},
   459→	handler mcp.ToolHandler,
   460→) {
   461→	s.mcpServer.AddTool(
   462→		&mcp.Tool{
   463→			Name:        name,
   464→			Description: description,
   465→			InputSchema: schema,
   466→		},
   467→		handler,
   468→	)
   469→}
   470→
   471→// loggingMiddleware wraps an HTTP handler with request logging
   472→func loggingMiddleware(next http.Handler) http.Handler {
   473→	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
   474→		start := time.Now()
   475→
   476→		log.Printf("[MCP] %s %s from %s", r.Method, r.URL.Path, r.RemoteAddr)
   477→
   478→		// Log headers for debugging
   479→		if sessionID := r.Header.Get("x-mcp-session-id"); sessionID != "" {
   480→			log.Printf("[MCP] Session ID: %s", sessionID)
   481→		}
   482→
   483→		// Call the actual handler
   484→		next.ServeHTTP(w, r)
   485→
   486→		log.Printf("[MCP] %s %s completed in %v", r.Method, r.URL.Path, time.Since(start))
   487→	})
   488→}
   489→
   490→// Start starts the MCP server
   491→func (s *Server) Start(ctx context.Context) error {
   492→	// Create HTTP/SSE handler for MCP protocol with JSON response option
   493→	opts := &mcp.StreamableHTTPOptions{
   494→		JSONResponse: true, // Return JSON instead of SSE
   495→	}
   496→	handler := mcp.NewStreamableHTTPHandler(
   497→		func(*http.Request) *mcp.Server { return s.mcpServer },
   498→		opts,
   499→	)
   500→
   501→	// Wrap handler with logging middleware
   502→	loggedHandler := loggingMiddleware(handler)
   503→
   504→	// Create HTTP server
   505→	mux := http.NewServeMux()
   506→	mux.Handle(
   507→		"/mcp",
   508→		loggedHandler,
   509→	)
   510→
   511→	// Add health check endpoint
   512→	mux.HandleFunc(
   513→		"/health",
   514→		func(
   515→			w http.ResponseWriter,
   516→			r *http.Request,
   517→		) {
   518→			w.WriteHeader(http.StatusOK)
   519→			fmt.Fprintf(
   520→				w,
   521→				"OK\n",
   522→			)
   523→		},
   524→	)
   525→
   526→	s.httpServer = &http.Server{
   527→		Addr: fmt.Sprintf(
   528→			"127.0.0.1:%d",
   529→			s.port,
   530→		),
   531→		Handler: mux,
   532→	}
   533→
   534→	log.Printf(
   535→		"Starting MCP evaluation server on http://127.0.0.1:%d/mcp\n",
   536→		s.port,
   537→	)
   538→	log.Printf(
   539→		"Health check endpoint: http://127.0.0.1:%d/health\n",
   540→		s.port,
   541→	)
   542→
   543→	// Start server in a goroutine
   544→	errChan := make(
   545→		chan error,
   546→		1,
   547→	)
   548→	go func() {
   549→		if err := s.httpServer.ListenAndServe(); err != nil && err != http.ErrServerClosed {
   550→			errChan <- err
   551→		}
   552→	}()
   553→
   554→	// Wait for context cancellation or server error
   555→	select {
   556→	case err := <-errChan:
   557→		return fmt.Errorf(
   558→			"server error: %w",
   559→			err,
   560→		)
   561→	case <-ctx.Done():
   562→		return s.Shutdown(context.Background())
   563→	}
   564→}
   565→
   566→// Shutdown gracefully shuts down the server
   567→func (s *Server) Shutdown(ctx context.Context) error {
   568→	log.Println("Shutting down MCP server...")
   569→
   570→	if s.httpServer == nil {
   571→		return nil
   572→	}
   573→
   574→	// Create a context with timeout for shutdown
   575→	shutdownCtx, cancel := context.WithTimeout(
   576→		ctx,
   577→		10*time.Second,
   578→	)
   579→	defer cancel()
   580→
   581→	return s.httpServer.Shutdown(shutdownCtx)
   582→}
   583→

